UPDATE fish_pet SET level = CASE WHEN level < 29 AND exp + 1 >= 100 THEN level + 1 WHEN level = 29 AND exp + 1 >= 100 THEN 30 ELSE level END, exp = CASE WHEN level = 29 AND exp + 1 >= 100 THEN 0 WHEN level < 29 AND exp + 1 >= 100 THEN 0 WHEN level < 30 THEN exp + 1 ELSE 100 END, hunger = CASE WHEN level = 29 AND exp + 1 >= 100 THEN 100 ELSE hunger END, mood = CASE WHEN level = 29 AND exp + 1 >= 100 THEN 100 ELSE mood END, updateTime = NOW() WHERE isDelete = 0 AND userId IN
#{userId}
AND (hunger > 0 OR mood > 0)
